import React, { useState, useEffect } from "react";
import email from "./image/email.png";
import linkedin from "./image/likedin.png";
import github from "./image/github.png";
import facebook from "./image/Facebook.png";
import instagram from "./image/instagram.png";

const styles = {
  mainContent: {
    background: "linear-gradient(135deg, #1f1f1f, #121212)",
    color: "#f0f0f0",
    textAlign: "center",
    padding: "2rem 1rem",
    minHeight: "100vh",
    position: "relative",
    fontFamily: "'Roboto', sans-serif",
  },
  logoContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "2rem",
    margin: "2rem 0",
    flexWrap: "wrap",
  },
  logo: {
    width: "60px",
    height: "60px",
    borderRadius: "50%",
    filter: "grayscale(100%)",
    cursor: "pointer",
    transition: "filter 0.3s, transform 0.3s",
  },
  envelopeContainer: {
    position: "relative",
    marginTop: "3rem",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "2rem",
  },
  envelope: {
    fontSize: "2.5rem",
    cursor: "pointer",
    transition: "transform 0.3s ease",
    position: "relative",
  },
  jokeBox: {
    position: "absolute",
    top: "-50px",
    left: "50%",
    transform: "translateX(-50%)",
    backgroundColor: "#333",
    color: "#fff",
    padding: "8px 12px",
    borderRadius: "8px",
    fontSize: "0.9rem",
    whiteSpace: "nowrap",
    opacity: 0,
    pointerEvents: "none",
    transition: "opacity 0.3s ease",
    textAlign: "center",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
  },
  footer: {
    marginTop: "4rem",
    fontSize: "0.85rem",
    color: "#888",
  },
};

const Portfolio = () => {
  const jokes = [
    "Why do programmers prefer dark mode? Because light attracts bugs!",
    "Why do Java developers wear glasses? Because they can't C#!",
    "I told my computer I needed a break, and now it won’t stop sending me KitKats.",
    "Why did the developer go broke? Because they used up all their cache!",
    "How do full-stack devs describe themselves? As 'in-divisible' units!",
    "What’s a programmer’s favorite type of music? Algo-rhythm!",
  ];

  const [hoveredJoke, setHoveredJoke] = useState(null);

  return (
    <div style={styles.mainContent}>
      <h1 style={{ fontSize: "2.5rem", margin: "1rem 0" }}>Contact Me</h1>

      {/* Social Media Links */}
      <div style={styles.logoContainer}>
        {[
          { href: "https://www.linkedin.com/in/mohammed-bakhshi/", imgSrc: linkedin, alt: "LinkedIn" },
          { href: "https://github.com/Mohammed-Bakhshi", imgSrc: github, alt: "GitHub" },
          { href: "https://www.facebook.com/mohammed.bakhshi/", imgSrc: facebook, alt: "Facebook" },
          { href: "https://instagram.com/mohammedbakhshi/", imgSrc: instagram, alt: "Instagram" },
          { href: "mailto:mohammedbakhshi@hotmail.com", imgSrc: email, alt: "Email" },
        ].map(({ href, imgSrc, alt }) => (
          <a key={alt} href={href} target="_blank" rel="noopener noreferrer">
            <img
              src={imgSrc}
              alt={alt}
              style={styles.logo}
              onMouseEnter={(e) => (e.target.style.filter = "grayscale(0%)")}
              onMouseLeave={(e) => (e.target.style.filter = "grayscale(100%)")}
            />
          </a>
        ))}
      </div>

      {/* Jokes Section */}
      <div style={styles.envelopeContainer}>
        {jokes.map((joke, index) => (
          <div
            key={index}
            style={styles.envelope}
            onMouseEnter={() => setHoveredJoke(index)}
            onMouseLeave={() => setHoveredJoke(null)}
          >
            📩
            {hoveredJoke === index && (
              <span style={styles.jokeBox}>{joke}</span>
            )}
          </div>
        ))}
      </div>

      <footer style={styles.footer}>
        Designed with ❤️ by Mohammed Bakhshi © {new Date().getFullYear()}
      </footer>
    </div>
  );
};

export default Portfolio;
