import React, { useState, useEffect } from 'react';

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        setIsVisible(entry.isIntersecting);
      },
      { threshold: 0.1 }
    );
    observer.observe(document.querySelector('#header'));
    return () => observer.disconnect();
  }, []);

  return (
    <div
      id="header"
      style={{
        ...styles.header,
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(-20px)',
        transition: 'opacity 0.6s ease-in-out, transform 0.6s ease-in-out',
      }}
    >
      <div style={styles.logoContainer}>
        <div style={styles.logo}></div>
        <h1 style={styles.name}>Mohammed Bakhshi</h1>
      </div>
      <h2 style={styles.title}>Hi, I'm Mohammed ðŸ‘‹</h2>
      <p style={styles.description}>
        With 7 years in tech, I bring passion and expertise to crafting
        user-friendly applications and solving complex problems. I thrive on
        challenges, whether in Leetcode, developing new skills, or competitive
        gaming.
      </p>
      <div style={styles.buttons}>
        <button style={styles.button}>About Me</button>
        <button style={styles.button}>Projects</button>
        <button style={styles.button}>View CV</button>
      </div>
    </div>
  );
};

const styles = {
  header: {
    textAlign: 'center',
    padding: '2rem 1rem',
    color: '#ffffff',
    backgroundColor: '#0d1117',
  },
  logoContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginBottom: '1rem',
  },
  logo: {
    width: '50px',
    height: '50px',
    border: '2px solid #c0c0c0', // Silvery border
    borderRadius: '8px', // Square with rounded edges
  },
  name: {
    fontSize: '2.5rem',
    marginTop: '0.5rem',
    color: '#c0c0c0', // Silvery metallic color
  },
  title: {
    fontSize: '2rem',
    fontWeight: 'bold',
    margin: '0.5rem 0',
  },
  description: {
    fontSize: '1rem',
    marginBottom: '1.5rem',
    lineHeight: '1.6',
    maxWidth: '600px',
    margin: '0 auto',
  },
  buttons: {
    display: 'flex',
    justifyContent: 'center',
    gap: '1rem',
  },
  button: {
    backgroundColor: '#007bff',
    color: '#ffffff',
    border: 'none',
    padding: '0.8rem 1.5rem',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonHover: {
    backgroundColor: '#0056b3',
  },
};

export default Header;
