import React, { useState } from "react";
import { motion } from "framer-motion";

const jokes = [
  "💀 Why don’t skeletons fight each other? They don’t have the guts.",
  "🍫 I told my computer I needed a break, now it won’t stop sending me KitKats.",
  "🌑 Why do programmers prefer dark mode? Because light attracts bugs! 🐛",
  "🐞 Why don’t programmers like nature? Too many bugs.",
  "😢 Why was the JavaScript developer sad? Because they didn't know how to 'null' their feelings.",
  "📴 How do functions break up? They stop calling each other. 📵",
];

const Envelope = ({ joke, onClick, isOpen }) => (
  <motion.div
    style={{
      fontSize: "60px",
      cursor: "pointer",
      textAlign: "center",
      position: "relative",
      padding: "10px",
    }}
    whileHover={{ scale: 1.2 }}
    onClick={onClick}
  >
    {isOpen ? (
      <motion.div
        style={{
          background: "#444",
          color: "#fff",
          borderRadius: "8px",
          padding: "10px",
          position: "absolute",
          top: "-50px",
          left: "50%",
          transform: "translateX(-50%)",
          zIndex: 1,
        }}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.8 }}
      >
        {joke}
      </motion.div>
    ) : (
      "📩"
    )}
  </motion.div>
);

const ContactPage = () => {
  const [openIndex, setOpenIndex] = useState(null);

  return (
    <div style={{ backgroundColor: "#171717", color: "#aaa", padding: "2rem" }}>
      <h1 style={{ textAlign: "center", color: "#fff" }}>Contact Me</h1>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(100px, 1fr))",
          gap: "20px",
          marginTop: "40px",
        }}
      >
        {jokes.map((joke, index) => (
          <Envelope
            key={index}
            joke={joke}
            isOpen={openIndex === index}
            onClick={() => setOpenIndex(openIndex === index ? null : index)}
          />
        ))}
      </div>
    </div>
  );
};

export default ContactPage;
