import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import anime from 'animejs/lib/anime.es.js';
import Footer from './footer';

const HomePage = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const textWrapperRef = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };

    const animateText = () => {
      if (textWrapperRef.current) {
        textWrapperRef.current.innerHTML = textWrapperRef.current.textContent.replace(/\S/g, "<span class='letter'>$&</span>");
        anime.timeline({ loop: true })
          .add({
            targets: '.ml13 .letter',
            translateY: [100, 0],
            opacity: [0, 1],
            easing: 'easeOutExpo',
            duration: 1400,
            delay: (el, i) => 30 * i,
          })
          .add({
            targets: '.ml13 .letter',
            translateY: [0, -100],
            opacity: [1, 0],
            easing: 'easeInExpo',
            duration: 1200,
            delay: (el, i) => 30 * i,
          });
      }
    };

    handleResize();
    animateText();

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const commonStyle = {
    color: '#aaa',
    backgroundColor: '#171717',
  };

  const buttonStyle = {
    fontSize: isMobileView ? '13px' : '18px',
    padding: '10px 20px',
    borderRadius: '8px',
    backgroundColor: 'transparent',
    color: '#aaa',
    border: '2px solid #aaa',
    textDecoration: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.3s, color 0.3s',
  };

  const buttonHoverStyle = {
    backgroundColor: '#333',
    color: '#fff',
  };

  return (
    <div style={{ ...commonStyle, minHeight: '100vh', textAlign: 'center', overflow: 'hidden' }}>
      <header style={{ marginTop: '10%', paddingBottom: '3rem' }}>
        <h1 style={{ fontSize: isMobileView ? '30px' : '50px', fontWeight: 'bold' }}>Hi, I'm Mohammed ðŸ‘‹</h1>
        <p style={{ fontSize: isMobileView ? '18px' : '24px' }}>I have been coding and analyzing data for 5+ years ðŸ¤–</p>
      </header>

      <section ref={textWrapperRef} className="ml13" style={{ margin: '2rem 0', fontSize: isMobileView ? '16px' : '20px' }}>
        Available to work<br />
        <span style={{ fontSize: '14px', color: '#bbb' }}>Flexible salary</span>
      </section>

      <div style={{ margin: '2rem', display: 'flex', justifyContent: 'center', gap: '1.5rem' }}>
        <Link
          to="/about"
          style={buttonStyle}
          onMouseEnter={(e) => e.target.style = { ...buttonStyle, ...buttonHoverStyle }}
          onMouseLeave={(e) => e.target.style = buttonStyle}
        >
          About Me
        </Link>
        <a
          href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
          download="Mohammed_Bakhshi_CV.pdf"
          target="_blank"
          rel="noopener noreferrer"
          style={buttonStyle}
          onMouseEnter={(e) => e.target.style = { ...buttonStyle, ...buttonHoverStyle }}
          onMouseLeave={(e) => e.target.style = buttonStyle}
        >
          View CV
        </a>
      </div>

      <Footer />
    </div>
  );
};

export default HomePage;
