import React, { useState, useEffect, useRef } from 'react';
import './Project.css'; // Assuming your CSS is named Project.css

const ProjectPage = () => {
  const [visibleSection, setVisibleSection] = useState(false);
  const projectContainerRef = useRef(null);

  // Intersection Observer to trigger fade-up animation for the project container
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setVisibleSection(true);
        }
      });
    }, {
      threshold: 0.5
    });

    if (projectContainerRef.current) {
      observer.observe(projectContainerRef.current);
    }

    return () => {
      if (projectContainerRef.current) {
        observer.unobserve(projectContainerRef.current);
      }
    };
  }, []);

  return (
    <div className="main">
      <div className={`project-container ${visibleSection ? 'fade-up' : ''}`} ref={projectContainerRef}>
        <h3 className="projects-header">My Projects</h3>
        <div className="projects-grid">
          {/* Project Cards */}
          <div className="project-card">
            <div className="project-icon">üéÆ</div>
            <div className="project-title">Mo‚Äôs Games</div>
            <div className="project-description">A fun gaming platform.</div>
            <a href="#" className="project-link">View Project</a>
          </div>
          <div className="project-card">
            <div className="project-icon">üíº</div>
            <div className="project-title">Clear Ear</div>
            <div className="project-description">A hearing clinic appointment manager.</div>
            <a href="#" className="project-link">View Project</a>
          </div>
          <div className="project-card">
            <div className="project-icon">üåç</div>
            <div className="project-title">Renuem</div>
            <div className="project-description">A renewable energy investment platform.</div>
            <a href="#" className="project-link">View Project</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectPage;
