import React, { useState, useEffect } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const mainContentStyle = {
  backgroundColor: '#171717',
  color: '#aaa',
  textAlign: 'center',
  padding: '2rem',
  minHeight: '100vh',
  overflowY: 'scroll',
  position: 'relative',
};

const logoStyle = {
  marginBottom: '20px',
  cursor: 'pointer',
  width: '50px',
  height: '50px',
  borderRadius: '50%',
  filter: 'grayscale(100%)',
  transition: 'filter 0.3s',
};

const logoContainerStyle = {
  display: 'flex',
  justifyContent: 'center',
  gap: '15px',
  margin: '30px 0',
};

const letterStyle = {
  fontSize: '2rem',
  cursor: 'pointer',
  transition: 'transform 0.2s ease',
  display: 'inline-block',
  position: 'relative',
};

const jokeStyle = {
  position: 'absolute',
  top: '-30px',
  left: '50%',
  transform: 'translateX(-50%)',
  backgroundColor: '#333',
  color: '#fff',
  padding: '5px 10px',
  borderRadius: '5px',
  fontSize: '0.9rem',
  whiteSpace: 'nowrap',
  opacity: 0,
  pointerEvents: 'none',
  transition: 'opacity 0.2s ease',
};

const Portfolio = () => {
  const [isMobileView, setIsMobileView] = useState(false);

  const jokes = [
    "Why don't skeletons fight each other? ðŸ¦´ They don't have the guts!",
    "Why did the scarecrow win an award? ðŸ† He was outstanding in his field!",
    "Why can't your nose be 12 inches long? ðŸ‘ƒ Because then it would be a foot!",
    "How does a penguin build its house? ðŸ§ Igloos it together!",
    "Why did the bicycle fall over? ðŸš² Because it was two-tired!",
    "What do you call fake spaghetti? ðŸ An impasta!",
  ];

  useEffect(() => {
    const checkIsMobile = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };
    checkIsMobile();
    window.addEventListener('resize', checkIsMobile);
    return () => window.removeEventListener('resize', checkIsMobile);
  }, []);

  return (
    <div style={{ position: 'relative' }}>
      <div style={mainContentStyle}>
        <h1>Contact Me</h1>
        
        {/* Social Media Links */}
        <div style={logoContainerStyle}>
          <a href="https://www.linkedin.com/in/mohammed-bakhshi/" target="_blank" rel="noopener noreferrer">
            <img
              src={linkedin}
              alt="LinkedIn"
              style={logoStyle}
              onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
            />
          </a>
          <a href="https://github.com/Mohammed-Bakhshi" target="_blank" rel="noopener noreferrer">
            <img
              src={github}
              alt="GitHub"
              style={logoStyle}
              onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
            />
          </a>
          <a href="https://www.facebook.com/mohammed.bakhshi/" target="_blank" rel="noopener noreferrer">
            <img
              src={facebook}
              alt="Facebook"
              style={logoStyle}
              onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
            />
          </a>
          <a href="https://instagram.com/mohammedbakhshi/" target="_blank" rel="noopener noreferrer">
            <img
              src={instagram}
              alt="Instagram"
              style={logoStyle}
              onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
            />
          </a>
          <a href="mailto:mohammedbakhshi@hotmail.com" target="_blank" rel="noopener noreferrer">
            <img
              src={email}
              alt="Email"
              style={logoStyle}
              onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
            />
          </a>
        </div>

        {/* Jokes Section */}
        <div style={{ marginTop: '40px' }}>
          <h2>Hover over a Letter for a Joke!</h2>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '15px', flexWrap: 'wrap', marginTop: '20px' }}>
            {jokes.map((joke, index) => (
              <span
                key={index}
                style={letterStyle}
                onMouseEnter={(e) => {
                  const jokeElement = e.target.querySelector('.joke-text');
                  jokeElement.style.opacity = 1;
                }}
                onMouseLeave={(e) => {
                  const jokeElement = e.target.querySelector('.joke-text');
                  jokeElement.style.opacity = 0;
                }}
              >
                ðŸ“©
                <span className="joke-text" style={jokeStyle}>{joke}</span>
              </span>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
