import React, { useEffect } from 'react';
import { Root, ColorSet, Label, RadarChart, AxisRendererCircular, ClockHand, AxisBullet, ValueAxis, AxisRange } from '@amcharts/amcharts5';
import { ease } from '@amcharts/amcharts5';
import { am5themes_Animated } from '@amcharts/amcharts5/themes/Animated';

const CustomGauge = () => {
  useEffect(() => {
    // Create root element
    const root = Root.new('chartdiv');

    // Set theme
    root.setThemes([am5themes_Animated.new(root)]);

    // Create chart
    const chart = root.container.children.push(
      RadarChart.new(root, {
        panX: false,
        panY: false,
        startAngle: 180,
        endAngle: 360
      })
    );

    // Create axis
    const axisRenderer = AxisRendererCircular.new(root, { innerRadius: -40 });
    const xAxis = chart.xAxes.push(
      ValueAxis.new(root, {
        maxDeviation: 0,
        min: 0,
        max: 100,
        strictMinMax: true,
        renderer: axisRenderer
      })
    );

    // Add clock hand
    const axisDataItem = xAxis.makeDataItem({});
    const clockHand = ClockHand.new(root, {
      pinRadius: 50,
      radius: 100,
      innerRadius: 50,
      bottomWidth: 0,
      topWidth: 0
    });

    // Clock hand appearance
    clockHand.pin.setAll({
      fillOpacity: 0,
      strokeOpacity: 0.5,
      stroke: am5.color(0x000000),
      strokeWidth: 1,
      strokeDasharray: [2, 2]
    });
    clockHand.hand.setAll({
      fillOpacity: 0,
      strokeOpacity: 0.5,
      stroke: am5.color(0x000000),
      strokeWidth: 0.5
    });

    // Bullet
    const bullet = axisDataItem.set('bullet', AxisBullet.new(root, { sprite: clockHand }));
    xAxis.createAxisRange(axisDataItem);

    // Label
    const label = chart.radarContainer.children.push(
      Label.new(root, {
        centerX: am5.percent(50),
        textAlign: 'center',
        centerY: am5.percent(50),
        fontSize: '1.5em'
      })
    );

    axisDataItem.set('value', 50);
    bullet.get('sprite').on('rotation', function () {
      const value = axisDataItem.get('value');
      label.set('text', Math.round(value).toString() + '%');
    });

    // Set interval to animate gauge value
    setInterval(function () {
      const value = Math.round(Math.random() * 100);
      axisDataItem.animate({
        key: 'value',
        to: value,
        duration: 500,
        easing: ease.out(ease.cubic)
      });
    }, 2000);

    // Set color ranges
    const colorSet = ColorSet.new(root, {});
    const axisRange0 = xAxis.createAxisRange(
      xAxis.makeDataItem({ above: true, value: 0, endValue: 50 })
    );
    axisRange0.get('axisFill').setAll({
      visible: true,
      fill: colorSet.getIndex(0)
    });
    axisRange0.get('label').setAll({
      forceHidden: true
    });

    const axisRange1 = xAxis.createAxisRange(
      xAxis.makeDataItem({ above: true, value: 50, endValue: 100 })
    );
    axisRange1.get('axisFill').setAll({
      visible: true,
      fill: colorSet.getIndex(4)
    });
    axisRange1.get('label').setAll({
      forceHidden: true
    });

    chart.appear(1000, 100);
  }, []);

  return (
    <div style={{ textAlign: 'center' }}>
      <div id="chartdiv" style={{ width: '100%', height: '500px' }}></div>
      <p style={{ marginTop: '10px', fontWeight: 'bold' }}># of Opportunities</p>
    </div>
  );
};

export default CustomGauge;
