import React, { useState, useEffect, useRef } from 'react';
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineOppositeContent,
} from '@mui/lab';
import Typography from '@mui/material/Typography';

import untitled from './image/Untitled.png';
import MosGames from './image/Mo\'s Games logo.png';
import mohammedGithub from './image/Mohammed_github.png';
import Nodejs from './image/node.png';
import theresidentsclub from './image/theresidentsclub.png';
import clearear from './image/Clearear.jpg';
import Javascript from './image/javascript.png';
import react from './image/React.png';
import Python from './image/python.png';
import SQL from './image/sql-logo.png';
import kotlin from './image/kotlin.png';
import selenium from './image/Selenium.png';
import renuem from './image/Renuem.png';

import './Aboutme.css';

const MainContent = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const [tooltipVisible, setTooltipVisible] = useState({});
  const [visibleSections, setVisibleSections] = useState({}); // Track visibility of sections

  const aboutRef = useRef(null); // Reference for the "About Me" section

  useEffect(() => {
    const updateView = () => setIsMobileView(window.innerWidth <= 1050);
    updateView();
    window.addEventListener('resize', updateView);
    return () => window.removeEventListener('resize', updateView);
  }, []);

  // Intersection Observer to trigger fade-up animation when section is in view
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setVisibleSections(prev => ({ ...prev, [entry.target.id]: true }));
        }
      });
    }, {
      threshold: 0.5 // Trigger when 50% of the section is in view
    });

    const fadeUpSection = aboutRef.current;
    if (fadeUpSection) {
      observer.observe(fadeUpSection);
    }

    return () => {
      if (fadeUpSection) {
        observer.unobserve(fadeUpSection);
      }
    };
  }, []);

  const handleLogoHover = (id) => {
    setTooltipVisible({ ...tooltipVisible, [id]: true });
  };

  const handleLogoLeave = (id) => {
    setTooltipVisible({ ...tooltipVisible, [id]: false });
  };

  const techLanguages = [
    { id: 'javascript', src: Javascript, name: 'Javascript' },
    { id: 'react', src: react, name: 'React.js' },
    { id: 'Node.js', src: Nodejs, name: 'Node.js' },
    { id: 'python', src: Python, name: 'Python' },
    { id: 'SQL', src: SQL, name: 'SQL' },
    { id: 'kotlin', src: kotlin, name: 'Kotlin' },
    { id: 'selenium', src: selenium, name: 'Selenium' },
  ];

  const workHistory = [
    {
      id: '2024-present',
      icon: renuem,
      title: 'üåç Renuem',
      description: (
        <>
          <p>Renewable energy investment platform.</p>
          <ul>
            <li>Developed a renewable energy investment app using React Native (Expo) and Node.js with Express.js, enabling individuals to invest in wind farms and solar panels through a scalable and secure platform.</li>
            <li>Deployed the backend on AWS, integrating MySQL RDS for efficient data management and real-time investment tracking.</li>
            <li>Collaborated with stakeholders to align the app with business goals, driving user engagement and satisfaction.</li>
          </ul>
        </>
      ),
    },
    {
      id: '2024',
      icon: clearear,
      title: 'üíº Clear Ear',
      description: (
        <>
          <p>Hearing clinic appointment management app.</p>
          <ul>
            <li>Designed and developed an Android app using React Native to manage patient appointments and streamline operations for a hearing clinic.</li>
            <li>Integrated a local database with AsyncStorage to store and track customer details and appointment history.</li>
            <li>Worked closely with clinic staff to gather requirements and ensure the app met operational needs and improved workflow efficiency.</li>
          </ul>
        </>
      ),
    },
    {
      id: '2020-2023',
      icon: MosGames,
      title: 'üéÆ Mo‚Äôs Pvt',
      description: (
        <>
          <p>Data analysis and digital development.</p>
          <ul>
            <li>Conducted data analysis for the launch of bubble tea in Pakistan, identifying key demographics and customer segments for targeted marketing.</li>
            <li>Analyzed market trends to assess the possibility of expanding into new locations for bubble tea and nitrogen ice cream shops.</li>
            <li>Developed and maintained the Mo‚Äôs Games website using HTML, CSS, and JavaScript, improving customer engagement and online presence.</li>
            <li>Utilized data insights to optimize marketing strategies, driving successful product launches and business growth.</li>
          </ul>
        </>
      ),
    },
  ];

  return (
    <div className="main">
      <div className={`bg ${isMobileView ? '' : 'wide'}`}>
        <div className={`hero-section ${isMobileView ? '' : 'row'}`}>
          {/* Image Section */}
          <div style={{ textAlign: 'center' }}>
            <img src={untitled} alt="Mohammed Bakhshi" />
            <div className="image-caption">Mohammed Bakhshi - Full Stack Developer</div>
          </div>

          {/* Divider */}
          {!isMobileView && <div className="divider"></div>}

          {/* Text Section */}
          <div className="paragraph">
            <p>
              With over 7 years of experience as a Full Stack Developer and Data Analyst, I‚Äôve built my career
              around designing efficient, user-focused solutions that bridge technical innovation with real-world impact.
              My expertise spans front-end and back-end development, leveraging tools like React Native, Node.js, and
              database management to create scalable applications for startups and enterprises.
            </p>
            <p>
              I‚Äôve also been fortunate to combine my technical skills with entrepreneurial insight. For instance, I played
              a key role in launching innovative ventures, such as introducing fruit bubble tea to the Pakistani market while
              managing a dynamic gaming lounge in Lahore. This experience sharpened my ability to analyze market trends, lead
              teams, and deliver customer-centric solutions.
            </p>
            <p>
              Outside of work, I‚Äôm always exploring the latest in technology, tackling coding challenges, and pursuing hobbies
              like gaming, photography, and solving puzzles. I believe in continually refining my skills while staying grounded
              in creativity and innovation.
            </p>
            <p>
              If you‚Äôre interested in learning more about my projects or discussing collaboration opportunities, feel free to
              explore further.
            </p>
          </div>
        </div>

        <h3 className={`section-header ${isMobileView ? '' : 'wide'}`}>
          Tech Languages
        </h3>
        <div className="logo-container">
          {techLanguages.map(({ id, src, name }) => (
            <div
              key={id}
              style={{ position: 'relative' }}
              onMouseEnter={() => handleLogoHover(id)}
              onMouseLeave={() => handleLogoLeave(id)}
            >
              <img
                id={id}
                src={src}
                alt={name}
                className={isMobileView ? '' : 'wide'}
              />
              <div className={`tooltip ${tooltipVisible[id] ? 'visible' : ''}`}>{name}</div>
            </div>
          ))}
        </div>

        <h3 className={`section-header ${isMobileView ? '' : 'wide'}`} ref={aboutRef}>
          Work History
        </h3>
        <Timeline position={isMobileView ? 'right' : 'alternate'}>
          {workHistory.map(({ id, icon, title, description }) => (
            <TimelineItem key={id}>
              <TimelineOppositeContent
                sx={{ m: 'auto 0' }}
                align="right"
                variant="body2"
                color="#808080"
              >
                {id}
              </TimelineOppositeContent>
              <TimelineSeparator>
                <img src={icon} alt={title} className="timeline-icon" />
                <TimelineConnector />
                <TimelineConnector />
              </TimelineSeparator>
              <TimelineContent sx={{ py: '12px', px: 2 }} className={visibleSections[aboutRef.current?.id] ? 'fade-up' : ''}>
                <Typography variant="h6" component="span">
                  {title}
                </Typography>
                <Typography>{description}</Typography>
              </TimelineContent>
            </TimelineItem>
          ))}
        </Timeline>
      </div>
    </div>
  );
};

export default MainContent;
