import React, { useState } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const Portfolio = () => {
  const [hoveredEnvelope, setHoveredEnvelope] = useState(null);

  const jokes = [
    "Why do programmers prefer dark mode? Because light attracts bugs!",
    "Why do Java developers wear glasses? Because they can't C#!",
    "I told my computer I needed a break, and now it wonâ€™t stop sending me KitKats.",
    "Why did the developer go broke? Because they used up all their cache!",
    "How do full-stack devs describe themselves? As 'in-divisible' units!",
    "Whatâ€™s a programmerâ€™s favorite type of music? Algo-rhythm!"
  ];

  const icons = [
    { src: linkedin, alt: "LinkedIn", href: "https://www.linkedin.com/in/mohammed-bakhshi/" },
    { src: github, alt: "GitHub", href: "https://github.com/Mohammed-Bakhshi" },
    { src: facebook, alt: "Facebook", href: "https://www.facebook.com/mohammed.bakhshi/" },
    { src: instagram, alt: "Instagram", href: "https://instagram.com/mohammedbakhshi/" },
    { src: email, alt: "Email", href: "mailto:mohammedbakhshi@hotmail.com" },
  ];

  const envelopeStyle = {
    fontSize: '3rem',
    cursor: 'pointer',
    position: 'absolute',
  };

  const jokeBoxStyle = {
    position: 'absolute',
    padding: '10px',
    backgroundColor: '#fff',
    color: '#333',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    zIndex: 10,
    textAlign: 'center',
    width: '200px',
  };

  const generateRandomPosition = () => ({
    top: `${Math.random() * 80}vh`,
    left: `${Math.random() * 80}vw`,
  });

  return (
    <div style={{ position: 'relative', width: '100%', height: '100vh', backgroundColor: '#171717', color: '#aaa' }}>
      <h1 style={{ textAlign: 'center', marginTop: '20px' }}>Contact Me</h1>
      
      {/* Social Icons Container centered */}
      <div style={{ 
        position: 'absolute', 
        top: '50%', 
        left: '50%', 
        transform: 'translate(-50%, -50%)', 
        display: 'flex', 
        justifyContent: 'center', 
        gap: '20px'
      }}>
        {icons.map((icon, index) => (
          <a key={index} href={icon.href} target="_blank" rel="noopener noreferrer">
            <img
              src={icon.src}
              alt={icon.alt}
              style={{
                width: '50px',
                height: '50px',
                borderRadius: '50%',
                transition: 'filter 0.3s',
                filter: 'grayscale(100%)',
                cursor: 'pointer',
              }}
              onMouseEnter={(e) => e.target.style.filter = 'grayscale(0%)'}
              onMouseLeave={(e) => e.target.style.filter = 'grayscale(100%)'}
            />
          </a>
        ))}
      </div>
      
      {/* Envelopes with jokes */}
      {jokes.map((joke, index) => {
        const position = generateRandomPosition();
        return (
          <div
            key={index}
            style={{ ...envelopeStyle, ...position }}
            onMouseEnter={() => setHoveredEnvelope(index)}
            onMouseLeave={() => setHoveredEnvelope(null)}
          >
            ðŸ“©
            {hoveredEnvelope === index && (
              <div style={{ ...jokeBoxStyle, ...position }}>
                {joke}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Portfolio;
