import React from 'react';
import './Project.css'; // Import your CSS for styling
import Top30Artist from './image/Top 30 artist .jpg';
import Calandar from './image/Calandar.jpg';
import Dashbord from './image/Excel Dashborad.jpg'; // Still imported here

const Project = () => {
  const projects = [
    {
      id: 1,
      imageUrl: Top30Artist, // Use the imported image
      shortDescription: 'Billboard Hot 100 Analysis.',
      longDescription: 'This project involves creating a dynamic and responsive web application with React.',
      githubLink: 'https://github.com/your-repo/project1',
      
    },
    {
      id: 2,
      imageUrl: Calandar,
      shortDescription: 'Clear ear patient/appoint tracking app.',
      longDescription: 'I analyzed the Billboard Hot 100 over decades(1970-2024). The process involved using Python\'s Pandas library to put the data into an easy-to-read data frame.',
      githubLink: 'https://github.com/your-repo/project2',
      
    },
    {
      id: 3,
      imageUrl: Dashbord, // This is a video file
      shortDescription: 'A UI-focused project.',
      longDescription: 'An interactive web design project focused on UX and responsive design principles.',
      githubLink: 'https://github.com/your-repo/project3',
      
    },
  ];

  return (
    <div className="project-container">
      <h1 className="projects-header">My Projects</h1>
      <div className="projects-grid">
        {projects.map((project) => (
          <div key={project.id} className="project-card">
            {/* Conditional rendering for images and videos */}
            {project.isVideo ? (
              <video
                controls
                className="project-video"
                src={project.imageUrl}
                alt={`Project ${project.id}`}
              >
                Your browser does not support the video tag.
              </video>
            ) : (
              <img
                src={project.imageUrl}
                alt={`Project ${project.id}`}
                className="project-image"
              />
            )}
            <div className="project-info">
              <p className="project-short-desc">{project.shortDescription}</p>
              <p className="project-long-desc">{project.longDescription}</p>
              <a
                href={project.githubLink}
                target="_blank"
                rel="noopener noreferrer"
                className="project-link"
              >
                View on GitHub
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Project;
