import React from "react";
import GaugeChart from "react-gauge-chart";

const CustomGauge = () => {
  return (
    <div style={{ textAlign: "center" }}>
      {/* Gauge Chart */}
      <GaugeChart
        id="gauge-chart-example"
        nrOfLevels={30} // Fewer levels for smoother transitions
        arcsLength={[0.6, 0.4]} // 60% filled, 40% empty
        colors={["#1E90FF", "#EAEAEA"]} // Filled (blue) and empty (gray)
        percent={0.6} // Fill 60% of the gauge
        arcPadding={0.02} // Padding between arcs
        hideText={true} // Removes text and the arrow
        arcWidth={0.2} // Adjust arc thickness
        style={{ width: "300px" }} // Chart width
      />
      <p style={{ marginTop: "10px", fontWeight: "bold" }}># of Opportunities</p>
    </div>
  );
};

export default CustomGauge;
