import React, { useState, useEffect } from 'react';
import './Aboutme.css';

import renuem from './image/Renuem.png';
import clearear from './image/Clearear.jpg';
import MosGames from './image/Mo\'s Games logo.png';

const MainContent = () => {
  const [isMobileView, setIsMobileView] = useState(false);

  useEffect(() => {
    const updateView = () => setIsMobileView(window.innerWidth <= 1050);
    updateView();
    window.addEventListener('resize', updateView);
    return () => window.removeEventListener('resize', updateView);
  }, []);

  const workHistory = [
    { id: '2024-present', icon: renuem, title: 'üåç Renuem' },
    { id: '2024', icon: clearear, title: 'üíº Clear Ear' },
    { id: '2020-2023', icon: MosGames, title: 'üéÆ Mo‚Äôs Pvt' },
  ];

  return (
    <div className="main">
      <div className="bg">
        <div className="work-history-section">
          <h3 className="section-header">Work History</h3>
          <div className="work-cards-container">
            {workHistory.map(({ id, icon, title }) => (
              <div key={id} className="work-card">
                <img className="work-icon" src={icon} alt={`${title} logo`} />
                <h4>{title}</h4>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainContent;
