import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import anime from 'animejs/lib/anime.es.js';
import Footer from './footer';

const HomePage = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const textWrapperRef = useRef(null);

  // To handle text animation effect
  const typeWriterEffect = (element, text, speed = 100) => {
    let i = 0;
    function type() {
      if (i < text.length) {
        element.innerHTML += text.charAt(i);
        i++;
        setTimeout(type, speed);
      }
    }
    type();
  };

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };

    handleResize();

    // Initializing typing effect
    const introText = 'Hi, I\'m Mohammed ðŸ‘‹ I have been coding and analyzing data for 5+ years ðŸ¤–';
    typeWriterEffect(document.getElementById('introText'), introText, 80);

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Styles for the page
  const commonStyle = {
    color: '#00FF00',  // Classic green text
    backgroundColor: '#1A1A1A', // Dark background
    minHeight: '100vh',
    fontFamily: "'Courier New', Courier, monospace",
    textAlign: 'center',
    padding: '20px',
  };

  const pipBoyFrameStyle = {
    border: '5px solid #00FF00',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 0 10px 0 rgba(0, 255, 0, 0.5)',
    margin: '0 auto',
    maxWidth: '80%',
    position: 'relative',
  };

  const sidebarStyle = {
    position: 'fixed',
    right: '10px',
    top: '20%',
    backgroundColor: '#2D2D2D',
    color: '#00FF00',
    padding: '20px',
    width: '200px',
    height: 'auto',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 255, 0, 0.5)',
    zIndex: '10',
  };

  const buttonStyle = {
    padding: '10px 20px',
    border: '2px solid #00FF00',
    color: '#00FF00',
    textDecoration: 'none',
    borderRadius: '5px',
    fontSize: '18px',
    transition: 'background-color 0.3s, color 0.3s',
  };

  const systemLogStyle = {
    position: 'absolute',
    bottom: '0',
    left: '0',
    width: '100%',
    color: '#00FF00',
    fontFamily: "'Courier New', Courier, monospace",
    padding: '10px',
    backgroundColor: '#171717',
  };

  return (
    <div style={commonStyle}>
      {/* Pip-Boy Frame around the page */}
      <div style={pipBoyFrameStyle}>
        <header style={{ textAlign: 'center', marginBottom: '30px' }}>
          <h1 id="introText" style={{ fontSize: isMobileView ? '25px' : '40px', fontWeight: 'bold' }}></h1>
          {/* This section will be typed out dynamically using the typewriter effect */}
        </header>

        <section ref={textWrapperRef} className="ml13" style={{ margin: '2rem 0' }}>
          Available to work<br />
          <span style={{ fontSize: '16px', color: '#bbb' }}>Flexible salary</span>
        </section>

        <div style={{ margin: '2rem', display: 'flex', justifyContent: 'center', gap: '1rem' }}>
          <Link
            to="/about"
            style={buttonStyle}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#003300';
              e.target.style.color = '#00FF00';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#00FF00';
            }}
          >
            About Me
          </Link>
          <a
            href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
            download="Mohammed_Bakhshi_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
            style={buttonStyle}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#003300';
              e.target.style.color = '#00FF00';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#00FF00';
            }}
          >
            View CV
          </a>
        </div>
      </div>

      {/* Sidebar with Interests moved to right */}
      <div style={sidebarStyle}>
        <h2>Interests</h2>
        <ul>
          <li>Web Development</li>
          <li>Data Science</li>
          <li>Machine Learning</li>
          <li>Gaming</li>
          <li>Reading</li>
        </ul>
      </div>

      {/* System Log */}
      <div style={systemLogStyle}>
        <p>System Log: <span>Booting...</span></p>
        <p><span>Welcome back, User.</span></p>
        <p><span>All systems operational.</span></p>
      </div>

      <Footer />
    </div>
  );
};

export default HomePage;
