import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Footer from './footer';

const HomePage = () => {
  const [isMobileView, setIsMobileView] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const commonStyle = {
    backgroundColor: '#0D1117',
    color: '#C9D1D9',
    minHeight: '100vh',
    fontFamily: "'Roboto', sans-serif",
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflowX: 'hidden',
  };

  const containerStyle = {
    display: 'grid',
    gridTemplateColumns: isMobileView ? '1fr' : '1fr 1fr',
    alignItems: 'center',
    gap: '20px',
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
  };

  const headerStyle = {
    marginBottom: '20px',
    color: '#58A6FF',
  };

  const sectionStyle = {
    padding: '20px',
    backgroundColor: '#161B22',
    borderRadius: '12px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  };

  const buttonStyle = {
    padding: '15px 30px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    color: '#FFFFFF',
    background: 'linear-gradient(90deg, #1F6FEB, #58A6FF)',
    cursor: 'pointer',
    marginTop: '20px',
    textDecoration: 'none',
    display: 'inline-block',
    transition: 'transform 0.3s',
  };

  const sidePanelStyle = {
    backgroundColor: '#21262D',
    color: '#C9D1D9',
    padding: '20px',
    borderRadius: '12px',
    height: '100%',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  };

  return (
    <div style={commonStyle}>
      <div style={containerStyle}>
        {/* Left Content */}
        <div style={sectionStyle}>
          <h1 style={headerStyle}>Hi, I'm Mohammed ðŸ‘‹</h1>
          <p>
            I am a passionate <strong>Full Stack Developer</strong> with over 5 years of experience in
            coding, analyzing data, and solving complex problems.
          </p>
          <Link to="/about" style={buttonStyle}>
            About Me
          </Link>
          <a
            href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
            download="Mohammed_Bakhshi_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
            style={buttonStyle}
          >
            View CV
          </a>
        </div>

        {/* Right Panel */}
        <div style={sidePanelStyle}>
          <h3>Skills & Interests</h3>
          <ul style={{ textAlign: 'left', lineHeight: '1.8' }}>
            <li>Web Development</li>
            <li>Data Science</li>
            <li>Machine Learning</li>
            <li>Gaming</li>
            <li>Reading</li>
          </ul>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default HomePage;
