import React, { useEffect, useState } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

// Apply fade-up effect with IntersectionObserver
const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        if (entry.isIntersecting) {
          setIsVisible(true);
        } else {
          setIsVisible(false);
        }
      },
      { threshold: 0.1 }
    );

    observer.observe(document.querySelector('#header'));

    return () => observer.disconnect();
  }, []);

  return (
    <div
      id="header"
      style={{
        ...styles.header,
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(-20px)',
        transition: 'opacity 0.6s ease-out, transform 0.6s ease-out',
      }}
    >
      <div style={styles.headerText}>Mohammed Bakhshi</div>

      <div style={styles.logoContainer}>
        {[{ href: 'https://www.linkedin.com/in/mohammed-bakhshi/', imgSrc: linkedin, alt: 'LinkedIn' },
          { href: 'https://github.com/Mohammed-Bakhshi', imgSrc: github, alt: 'GitHub' },
          { href: 'https://www.facebook.com/mohammed.bakhshi/', imgSrc: facebook, alt: 'Facebook' },
          { href: 'https://instagram.com/mohammedbakhshi/', imgSrc: instagram, alt: 'Instagram' },
          { href: 'mailto:mohammedbakhshi@hotmail.com', imgSrc: email, alt: 'Email' }]
          .map(({ href, imgSrc, alt }) => (
            <a key={alt} href={href} target="_blank" rel="noopener noreferrer" style={styles.logoLink}>
              <img
                src={imgSrc}
                alt={alt}
                style={styles.logo}
                onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
                onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
              />
            </a>
          ))}
      </div>
    </div>
  );
};

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px 30px', // Adjusted padding for better balance
    position: 'sticky',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 999,
    backgroundColor: 'rgba(0, 0, 0, 0.8)', // Darker, slightly opaque background for contrast
    boxShadow: '0 2px 10px rgba(0, 0, 0, 0.2)', // Subtle shadow to give depth
    width: '100%',
    transition: 'all 0.3s ease', // Smooth transition for the background
  },

  headerText: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#fff',
    letterSpacing: '2px', // Space out the letters for a sleek effect
    textTransform: 'uppercase', // Adds a modern touch
    transition: 'all 0.3s ease', // Smooth text transition when hover/scroll
  },

  logoContainer: {
    display: 'flex',
    justifyContent: 'space-evenly',
    alignItems: 'center',
  },

  logoLink: {
    display: 'inline-block',
    marginLeft: '15px', // Uniform space between logos
    transition: 'all 0.3s ease',
  },

  logo: {
    width: '35px',
    height: '35px',
    borderRadius: '50%',
    filter: 'grayscale(100%)',
    cursor: 'pointer',
    transition: 'filter 0.3s, transform 0.3s ease',
  },
};

export default Header;
