import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Footer from "./footer";

const HomePage = () => {
  const [isMobileView, setIsMobileView] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const commonStyle = {
    color: "#E5E5E5",
    backgroundColor: "#121212",
    minHeight: "100vh",
    fontFamily: "'Poppins', sans-serif",
    textAlign: "center",
    padding: "0 20px",
    boxSizing: "border-box",
    overflow: "hidden",
  };

  const headerStyle = {
    textAlign: "center",
    marginBottom: "30px",
    animation: "fadeIn 2s ease-in",
  };

  const cardStyle = {
    backgroundColor: "#1E1E1E",
    color: "#E5E5E5",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
    maxWidth: "450px",
    margin: "20px auto",
  };

  const buttonStyle = {
    padding: "15px 30px",
    border: "none",
    borderRadius: "5px",
    fontSize: "16px",
    fontWeight: "600",
    color: "#fff",
    backgroundColor: "#3A86FF",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  };

  const buttonContainerStyle = {
    display: "flex",
    justifyContent: "space-between",
    gap: "1rem",
    marginTop: "20px",
  };

  const imageContainerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    margin: "40px 0",
  };

  return (
    <div style={commonStyle}>
      <header style={headerStyle}>
        <h1 style={{ fontSize: isMobileView ? "30px" : "45px", fontWeight: "bold" }}>
          Hi, I'm Mohammed ðŸ‘‹
        </h1>
        <p style={{ fontSize: isMobileView ? "18px" : "22px", marginTop: "10px" }}>
          I am a passionate Full Stack Developer with over 5 years of experience in coding, analyzing data, and solving complex problems.
        </p>
      </header>

      <div style={cardStyle}>
        <h2>Skills & Interests</h2>
        <ul style={{ listStyle: "none", padding: 0, textAlign: "left" }}>
          <li>Web Development</li>
          <li>Data Science</li>
          <li>Machine Learning</li>
          <li>Gaming</li>
          <li>Reading</li>
        </ul>
        <div style={buttonContainerStyle}>
          <Link to="/about">
            <button style={buttonStyle}>About Me</button>
          </Link>
          <a
            href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
            download="Mohammed_Bakhshi_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
          >
            <button style={buttonStyle}>View CV</button>
          </a>
        </div>
      </div>

      <div style={imageContainerStyle}>
        {/* Replace the src below with your preferred GIF or image */}
        <img
          src="https://media.giphy.com/media/LmNwrBhejkK9EFP504/giphy.gif"
          alt="Coding GIF"
          style={{ maxWidth: "100%", maxHeight: "300px", borderRadius: "10px" }}
        />
      </div>

      <Footer />
    </div>
  );
};

export default HomePage;
