import React, { useState, useEffect } from 'react';
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineOppositeContent,
} from '@mui/lab';
import Typography from '@mui/material/Typography';
import { motion } from 'framer-motion';

import untitled from './image/Untitled.png';
import MosGames from './image/Mo\'s Games logo.png';
import mohammedGithub from './image/Mohammed_github.png';
import Nodejs from './image/node.png';
import theresidentsclub from './image/theresidentsclub.png';
import clearear from './image/Clearear.jpg';
import Javascript from './image/javascript.png';
import react from './image/React.png';
import Python from './image/python.png';
import SQL from './image/sql-logo.png';
import kotlin from './image/kotlin.png';
import selenium from './image/Selenium.png';
import renuem from './image/Renuem.png';

const MainContent = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const [tooltipVisible, setTooltipVisible] = useState({});

  useEffect(() => {
    const updateView = () => setIsMobileView(window.innerWidth <= 1050);
    updateView();
    window.addEventListener('resize', updateView);
    return () => window.removeEventListener('resize', updateView);
  }, []);

  const handleLogoHover = (id) => {
    setTooltipVisible({ ...tooltipVisible, [id]: true });
  };

  const handleLogoLeave = (id) => {
    setTooltipVisible({ ...tooltipVisible, [id]: false });
  };

  const styles = {
    main: {
      paddingLeft: '300px',
      paddingRight: '300px',
      background: 'linear-gradient(to bottom, #253041, #2a2858)',
      color: '#C9D1D9',
      minHeight: '100vh',
      fontFamily: "'Roboto', sans-serif",
      boxSizing: 'border-box',
    },
    heroSection: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '2rem',
      flexDirection: isMobileView ? 'column' : 'row',
      marginTop: '6rem',
      padding: '0 2rem',
    },
    image: {
      width: isMobileView ? '50%' : '15%',
      borderRadius: '5%',
    },
    paragraph: {
      fontSize: isMobileView ? '18px' : '20px',
      textAlign: 'left',
      lineHeight: '1.8',
      marginTop: '50px',
      maxWidth: isMobileView ? '95%' : '50%',
    },
    sectionHeader: {
      textAlign: 'center',
      margin: '4rem 0 2rem',
      color: '#aaa',
      fontWeight: 'normal',
      fontSize: isMobileView ? '1.2rem' : '1.5rem',
    },
    logoContainer: {
      display: 'flex',
      justifyContent: 'space-evenly',
      gap: '2rem',
      margin: '2rem 0',
    },
    logo: {
      width: isMobileView ? '30px' : '50px',
      filter: 'grayscale(100%)',
      transition: 'filter 0.3s ease-in-out',
    },
    tooltip: {
      position: 'absolute',
      backgroundColor: '#555',
      color: '#fff',
      padding: '5px 10px',
      borderRadius: '6px',
      fontSize: '12px',
      bottom: '130%',
      left: '50%',
      transform: 'translateX(-50%)',
      whiteSpace: 'nowrap',
      zIndex: '1',
      visibility: 'hidden',
      opacity: 0,
      transition: 'opacity 0.2s ease-in-out',
    },
    tooltipVisible: {
      visibility: 'visible',
      opacity: 1,
    },
  };

  const fadeUpAnimation = {
    initial: { opacity: 0, y: 50 },
    animate: { opacity: 1, y: 0 },
    transition: { duration: 0.8, ease: 'easeOut' },
  };

  const techLanguages = [
    { id: 'javascript', src: Javascript, name: 'Javascript' },
    { id: 'react', src: react, name: 'React.js' },
    { id: 'Node.js', src: Nodejs, name: 'Node.js' },
    { id: 'python', src: Python, name: 'Python' },
    { id: 'SQL', src: SQL, name: 'SQL' },
    { id: 'kotlin', src: kotlin, name: 'Kotlin' },
    { id: 'selenium', src: selenium, name: 'Selenium' },
  ];

  const workHistory = [
    {
      id: '2024-present',
      icon: renuem,
      title: 'üåç Renuem',
      description: (
        <>
          <p>Renewable energy investment platform.</p>
          <ul>
            <li>Developed a renewable energy investment app...</li>
            <li>Deployed the backend on AWS...</li>
            <li>Collaborated with stakeholders...</li>
          </ul>
        </>
      ),
    },
    {
      id: '2024',
      icon: clearear,
      title: 'üíº Clear Ear',
      description: (
        <>
          <p>Hearing clinic appointment management app.</p>
          <ul>
            <li>Designed and developed an Android app...</li>
            <li>Integrated a local database...</li>
            <li>Worked closely with clinic staff...</li>
          </ul>
        </>
      ),
    },
    {
      id: '2020-2023',
      icon: MosGames,
      title: 'üéÆ Mo‚Äôs Pvt',
      description: (
        <>
          <p>Data analysis and digital development.</p>
          <ul>
            <li>Conducted data analysis for bubble tea launch...</li>
            <li>Analyzed market trends...</li>
            <li>Developed and maintained the Mo‚Äôs Games website...</li>
            <li>Utilized data insights to optimize strategies...</li>
          </ul>
        </>
      ),
    },
  ];

  return (
    <div style={styles.main}>
      <motion.div style={styles.heroSection} {...fadeUpAnimation}>
        <img src={untitled} alt="Mohammed" style={styles.image} />
        <div style={styles.paragraph}>
          <p>With over 7 years of experience...</p>
          <p>I‚Äôve also been fortunate...</p>
        </div>
      </motion.div>

      <motion.h3 style={styles.sectionHeader} {...fadeUpAnimation}>
        Tech Languages
      </motion.h3>
      <motion.div style={styles.logoContainer} {...fadeUpAnimation}>
        {techLanguages.map(({ id, src, name }) => (
          <div
            key={id}
            style={{ position: 'relative' }}
            onMouseEnter={() => handleLogoHover(id)}
            onMouseLeave={() => handleLogoLeave(id)}
          >
            <img
              id={id}
              src={src}
              alt={name}
              style={styles.logo}
              onMouseEnter={(e) => (e.currentTarget.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.currentTarget.style.filter = 'grayscale(100%)')}
            />
            <div
              style={{
                ...styles.tooltip,
                ...(tooltipVisible[id] ? styles.tooltipVisible : {}),
              }}
            >
              {name}
            </div>
          </div>
        ))}
      </motion.div>

      <motion.h3 style={styles.sectionHeader} {...fadeUpAnimation}>
        Work History
      </motion.h3>
      <Timeline position={isMobileView ? 'right' : 'alternate'}>
        {workHistory.map(({ id, icon, title, description }) => (
          <TimelineItem key={id}>
            <TimelineOppositeContent
              sx={{ m: 'auto 0' }}
              align="right"
              variant="body2"
              color="#808080"
            >
              {id}
            </TimelineOppositeContent>
            <TimelineSeparator>
              <TimelineConnector />
              <img src={icon} alt={title} style={{ width: '30px', borderRadius: '50%' }} />
              <TimelineConnector />
            </TimelineSeparator>
            <TimelineContent sx={{ py: '12px', px: 2 }}>
              <Typography variant="h6" component="span">{title}</Typography>
              <Typography>{description}</Typography>
            </TimelineContent>
          </TimelineItem>
        ))}
      </Timeline>
    </div>
  );
};

export default MainContent;
