import React, { useState, useEffect } from 'react';
import './ClearEar.css';
import clearEarImage1 from './image/Clearear.jpg';
import clearEarImage2 from './image/Clear-Ear-light-mode.jpg';
import clearEarImage3 from './image/Clear-Ear-View-patients.jpg';

const ClearEar = () => {
  const clearEarDetails = [
    "Designed and developed an Android app using React Native to manage patient appointments and streamline operations for a hearing clinic. ",
    "Integrated a local database with AsyncStorage to store and track customer details and appointment history.",
    "Worked closely with clinic staff to gather requirements and ensure the app met operational needs and improved workflow efficiency",
  ];

  const images = [clearEarImage1, clearEarImage2, clearEarImage3];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Automatically change slides every 2 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      handleNext();
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="clearear-container">
      {/* Image Slider */}
      <div className="clearear-image-container">
        <img
          src={images[currentImageIndex]}
          alt="ClearEar"
          className="clearear-image"
        />
        <button className="arrow arrow-left" onClick={handlePrev}>
          &#8249;
        </button>
        <button className="arrow arrow-right" onClick={handleNext}>
          &#8250;
        </button>
      </div>

      {/* Text Content */}
      <div className="clearear-content">
        <h1 className="clearear-title">ðŸ”Š ClearEar</h1>
        <p className="clearear-description">
          Personalized hearing aid app for enhanced audio experience.
        </p>

        {/* New Summary Section */}
        <h2>What is ClearEar?</h2>
        <p className="clearear-summary">
          ClearEar is a personalized hearing aid app designed to improve audio experiences for users with hearing impairments by offering real-time enhancement and noise reduction.
        </p>

        {/* Role Section */}
        <h2>What was my role in ClearEar?</h2>
        <p className="clearear-role-description">
          As a developer at ClearEar, I contributed to the design and implementation of the app using React Native and integrated machine learning models for audio enhancement.
        </p>

        {/* Additional Details */}
        <ul className="clearear-bullet-points">
          {clearEarDetails.map((detail, index) => (
            <li key={index}>{detail}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ClearEar;
