import React, { useEffect, useState } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        if (entry.isIntersecting) {
          setIsVisible(true);
        } else {
          setIsVisible(false);
        }
      },
      { threshold: 0.1 }
    );
    observer.observe(document.querySelector('#header'));
    return () => observer.disconnect();
  }, []);

  return (
    <div
      id="header"
      style={{
        ...styles.header,
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(-20px)',
        transition: 'opacity 0.6s ease-out, transform 0.6s ease-out',
      }}
    >
      {/* Header Text */}
      <div style={styles.headerText}>Mohammed Bakhshi</div>

      {/* Social Media Logos */}
      <div style={styles.logoContainer}>
        {[
          { href: 'https://www.linkedin.com/in/mohammed-bakhshi/', imgSrc: linkedin, alt: 'LinkedIn' },
          { href: 'https://github.com/Mohammed-Bakhshi', imgSrc: github, alt: 'GitHub' },
          { href: 'https://www.facebook.com/mohammed.bakhshi/', imgSrc: facebook, alt: 'Facebook' },
          { href: 'https://instagram.com/mohammedbakhshi/', imgSrc: instagram, alt: 'Instagram' },
          { href: 'mailto:mohammedbakhshi@hotmail.com', imgSrc: email, alt: 'Email' },
        ].map(({ href, imgSrc, alt }) => (
          <a key={alt} href={href} target="_blank" rel="noopener noreferrer" style={styles.logoLink}>
            <img
              src={imgSrc}
              alt={alt}
              style={styles.logo}
              onMouseEnter={(e) => (e.target.style.transform = 'scale(1.1)')}
              onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
            />
          </a>
        ))}
      </div>
    </div>
  );
};

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '30px 50px', // Slightly more balanced padding
    position: 'sticky',
    top: 0,
    width: '100%',
    backgroundColor: 'rgba(10, 10, 10, 0.8)', // Darker background with opacity for sleek look
    boxShadow: '0 6px 30px rgba(0, 0, 0, 0.3)', // Deeper shadow for more depth
    zIndex: 9999,
    transition: 'background-color 0.3s ease',
  },

  headerText: {
    fontSize: '3rem', // Slightly larger text for a bold statement
    fontWeight: '700',
    color: '#fff',
    letterSpacing: '2px', // More spacing for a modern look
    textTransform: 'uppercase',
    transition: 'color 0.3s ease',
  },

  logoContainer: {
    display: 'flex',
    justifyContent: 'space-evenly',
    alignItems: 'center',
    gap: '25px', // Space out logos evenly
  },

  logoLink: {
    display: 'inline-block',
    transition: 'all 0.3s ease',
  },

  logo: {
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    filter: 'brightness(0) saturate(100%)', // Logos in full color with a bright look
    cursor: 'pointer',
    transition: 'transform 0.3s ease, filter 0.3s ease',
  },
};

export default Header;
