import React, { useState, useEffect } from 'react';
import './Aboutme.css';

import renuem from './image/Renuem.png';
import clearear from './image/Clearear.jpg';
import MosGames from './image/Mo\'s Games logo.png';

const MainContent = () => {
  const [isMobileView, setIsMobileView] = useState(false);

  useEffect(() => {
    const updateView = () => setIsMobileView(window.innerWidth <= 1050);
    updateView();
    window.addEventListener('resize', updateView);
    return () => window.removeEventListener('resize', updateView);
  }, []);

  const workHistory = [
    { id: '2024-present', icon: renuem, title: 'üåç Renuem' },
    { id: '2024', icon: clearear, title: 'üíº Clear Ear' },
    { id: '2020-2023', icon: MosGames, title: 'üéÆ Mo‚Äôs Pvt' },
  ];

  const techStack = [
    { id: 'React', icon: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/React-icon.svg' },
    { id: 'Node', icon: 'https://upload.wikimedia.org/wikipedia/commons/7/73/Node.js_logo.svg' },
    { id: 'Python', icon: 'https://upload.wikimedia.org/wikipedia/commons/c/c3/Python-logo-notext.svg' },
    { id: 'Docker', icon: 'https://upload.wikimedia.org/wikipedia/commons/1/1f/Docker-logo.svg' },
    { id: 'MongoDB', icon: 'https://upload.wikimedia.org/wikipedia/commons/4/47/MongoDB-Logo.svg' },
    { id: 'MySQL', icon: 'https://upload.wikimedia.org/wikipedia/commons/6/67/MySQL_logo.png' },
    { id: 'GitHub', icon: 'https://upload.wikimedia.org/wikipedia/commons/9/91/Octicons-mark-github.svg' },
    { id: 'JavaScript', icon: 'https://upload.wikimedia.org/wikipedia/commons/6/64/JavaScript-logo.png' },
  ];

  return (
    <div className="main">
      {/* Hero Section */}
      <div className="hero-section">
        <img src="https://via.placeholder.com/150" alt="Profile" />
        <p className="paragraph">Hello, I‚Äôm a passionate developer with a knack for creating innovative solutions. I specialize in full-stack development, utilizing cutting-edge technologies to build robust and scalable applications.</p>
      </div>

      {/* Tech Stack Section */}
      <div className="tech-stack-section">
        <h3 className="section-header">Tech Stack</h3>
        <div className="tech-stack-icons">
          {techStack.map(({ id, icon }) => (
            <img key={id} src={icon} alt={id} />
          ))}
        </div>
      </div>

      {/* Work History Section */}
      <div className="work-history-section">
        <h3 className="section-header">Work History</h3>
        <div className="work-cards-container">
          {workHistory.map(({ id, icon, title }) => (
            <div key={id} className="work-card">
              <img className="work-icon" src={icon} alt={`${title} logo`} />
              <h4>{title}</h4>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MainContent;
