import React, { useState, useEffect } from 'react';
import './MosGames.css';
import mosGamesImage1 from "./image/Mo's Games logo.jpg";
import mosGamesImage2 from './image/MosGamesOpening.jpg';
import mosGamesImage3 from './image/MosGamesTournament.jpg';

const MosGames = () => {
  const mosGamesDetails = [
    "Conducted data analysis for the launch of bubble tea in Pakistan, identifying key demographics and customer segments for targeted marketing. ",
    "Integrated Firebase for user authentication, cloud storage, and dynamic game state management.",
    "Collaborated with designers to optimize the UI/UX for engaging user experiences.",
  ];

  const images = [mosGamesImage1, mosGamesImage2, mosGamesImage3];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Automatically change slides every 2 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      handleNext();
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="mosgames-container">
      {/* Image Slider */}
      <div className="mosgames-image-container">
        <img
          src={images[currentImageIndex]}
          alt="Mos Games"
          className="mosgames-image"
        />
        <button className="arrow arrow-left" onClick={handlePrev}>
          &#8249;
        </button>
        <button className="arrow arrow-right" onClick={handleNext}>
          &#8250;
        </button>
      </div>

      {/* Text Content */}
      <div className="mosgames-content">
        <h1 className="mosgames-title">ðŸŽ® Mos Games</h1>
        <p className="mosgames-description">
          Innovative gaming platform for all enthusiasts.
        </p>

        {/* New Summary Section */}
        <h2>What is Mos Games?</h2>
        <p className="mosgames-summary">
          Mos Games is a gaming company dedicated to creating multiplayer and immersive experiences that bring players together.
        </p>

        {/* Role Section */}
        <h2>What was my role in Mos Games?</h2>
        <p className="mosgames-role-description">
          At Mos Games, I spearheaded the development of a real-time multiplayer gaming app, leveraging cutting-edge technologies for seamless gameplay.
        </p>

        {/* Additional Details */}
        <ul className="mosgames-bullet-points">
          {mosGamesDetails.map((detail, index) => (
            <li key={index}>{detail}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MosGames;
