import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const mainContentStyle = {
  backgroundColor: '#171717',
  color: '#aaa',
  textAlign: 'center',
  padding: '2rem',
  minHeight: '100vh',
  overflowY: 'scroll',
  position: 'relative',
  scrollbarWidth: 'none',
  msOverflowStyle: 'none',
};

const grayscaleLogoStyle = {
  filter: 'grayscale(100%)',
  transition: 'filter 0.3s',
};

const Portfolio = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const textWrapperRef = useRef(null);

  useEffect(() => {
    const checkIsMobile = () => {
      const isMobile = window.innerWidth <= 1050;
      setIsMobileView(isMobile);
    };

    checkIsMobile();

    const handleResize = () => {
      const textWrapper = textWrapperRef.current;
      if (textWrapper) {
        // You can remove the anime.js animation here if you don't need it
      }
    };

    handleResize();

    window.addEventListener('resize', checkIsMobile);

    return () => {
      window.removeEventListener('resize', checkIsMobile);
    };
  }, []);

  const openCV = () => {
    window.open("https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf", "_blank");
  };

  const tooltipTextStyle = {
    visibility: 'hidden',
    width: 'auto',
    background: '#555',
    color: '#fff',
    textAlign: 'center',
    borderRadius: '6px',
    padding: '5px 10px',
    position: 'absolute',
    zIndex: '1',
    bottom: '130%',
    right: '50%',
    transform: 'translateX(50%)',
    opacity: '0',
    transition: 'opacity .2s ease-in',
    fontSize: '14px',
    whiteSpace: 'nowrap',
  };

  const logoStyle = {
    marginBottom: isMobileView ? '40px' : '90px',
    cursor: 'pointer',
    transition: 'filter 0.3s',
    width: isMobileView ? '30px' : '50px',
    height: isMobileView ? '30px' : '50px',
    borderRadius: '30%',
  };

  const logoContainerStyle = {
    display: 'flex',
    flexDirection: isMobileView ? 'row' : 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginLeft:isMobileView? '0%':'0%',
    gap:'5%',
    marginTop:isMobileView?'80%':'30%',
  };

  const envelopeStyle = {
    width: '100px',
    height: '60px',
    backgroundColor: '#888',
    borderRadius: '10px',
    position: 'relative',
    overflow: 'hidden',
    cursor: 'pointer',
    transition: 'transform 0.3s ease-in-out',
  };

  const flapStyle = {
    width: '100%',
    height: '50%',
    backgroundColor: '#444',
    position: 'absolute',
    top: '0',
    left: '0',
    transition: 'top 0.3s ease-in-out',
  };

  const jokeStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    opacity: '0',
    color: '#00ff00',
    fontSize: '14px',
    transition: 'opacity 0.3s ease-in-out',
  };

  return (
    <>
      <style>
        {`
          ::selection {
            color: #00e6e6;
          }
        `}
      </style>
      
      <div style={{ position: 'relative' }}>
        <div style={mainContentStyle}>
  
          <div style={{ fontSize: '20px', position:'relative', top:isMobileView?'100px':'200px', left:'0%' }}>
            <h1>Contact me</h1>
          </div>
          
          <div style={logoContainerStyle}>
            <div className="tooltip" style={{ position: 'relative', display: 'inline-block' }}>
              <a href="https://www.linkedin.com/in/mohammed-bakhshi/" target="_blank" rel="noopener noreferrer">
                <img
                  src={linkedin}
                  alt="LinkedIn"
                  style={Object.assign({}, logoStyle, grayscaleLogoStyle)}
                  onMouseEnter={(e) => { e.target.style.filter = 'grayscale(0%)'; e.target.nextSibling.style.color = '#0077B5'; }}
                  onMouseLeave={(e) => { e.target.style.filter = 'grayscale(100%)'; e.target.nextSibling.style.color = '#aaa'; }}
                />
                <span className="tooltip--text" style={tooltipTextStyle}>Hire me on LinkedIn</span>
              </a>
            </div>
            {/* Add more social media links here */}
          </div>

          <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', marginTop: '30px' }}>
            <div style={envelopeStyle} onMouseEnter={(e) => e.target.querySelector('.flap').style.top = '-50%'} onMouseLeave={(e) => e.target.querySelector('.flap').style.top = '0'}>
              <div className="flap" style={flapStyle}></div>
              <div className="joke" style={jokeStyle}>Why don’t skeletons fight each other? They don’t have the guts.</div>
            </div>

            <div style={envelopeStyle} onMouseEnter={(e) => e.target.querySelector('.flap').style.top = '-50%'} onMouseLeave={(e) => e.target.querySelector('.flap').style.top = '0'}>
              <div className="flap" style={flapStyle}></div>
              <div className="joke" style={jokeStyle}>I told my computer I needed a break, now it won’t stop sending me KitKats.</div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Portfolio;
