import React from "react";
import GaugeChart from "react-gauge-chart";

const CustomGauge = () => {
  return (
    <div style={{ textAlign: "center" }}>
      {/* Gauge Chart */}
      <GaugeChart
        id="gauge-chart-example"
        nrOfLevels={100} // Number of levels or segments in the chart
        arcsLength={[0.6, 0.4]} // Proportion of each segment
        colors={["#1E90FF", "#EAEAEA"]} // Colors of the segments
        percent={0.6} // Percentage of the gauge filled
        arcPadding={0.02} // Padding between arcs
        textColor="#000" // Text color
        style={{ width: "300px" }} // Chart width
      />
      <p style={{ marginTop: "10px", fontWeight: "bold" }}># of Opportunities</p>
    </div>
  );
};

export default CustomGauge;
