import React, { useEffect, useState } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        setIsVisible(entry.isIntersecting);
      },
      { threshold: 0.1 }
    );
    observer.observe(document.querySelector('#header'));
    return () => observer.disconnect();
  }, []);

  return (
    <div
      id="header"
      style={{
        ...styles.header,
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(-20px)',
        transition: 'opacity 0.6s ease-in-out, transform 0.6s ease-in-out',
      }}
    >
      <div style={styles.headerText}>
        Mohammed <span style={styles.gradientText}>Bakhshi</span>
      </div>

      <div style={styles.logoContainer}>
        {[
          { href: 'https://www.linkedin.com/in/mohammed-bakhshi/', imgSrc: linkedin, alt: 'LinkedIn' },
          { href: 'https://github.com/Mohammed-Bakhshi', imgSrc: github, alt: 'GitHub' },
          { href: 'https://www.facebook.com/mohammed.bakhshi/', imgSrc: facebook, alt: 'Facebook' },
          { href: 'https://instagram.com/mohammedbakhshi/', imgSrc: instagram, alt: 'Instagram' },
          { href: 'mailto:mohammedbakhshi@hotmail.com', imgSrc: email, alt: 'Email' },
        ].map(({ href, imgSrc, alt }) => (
          <a key={alt} href={href} target="_blank" rel="noopener noreferrer" style={styles.logoLink}>
            <img
              src={imgSrc}
              alt={alt}
              style={styles.logo}
              onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
              onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
            />
          </a>
        ))}
      </div>
    </div>
  );
};

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px 50px',
    position: 'sticky',
    top: 0,
    width: '100%',
    backgroundColor: 'rgba(10, 10, 10, 0.8)',
    backdropFilter: 'blur(10px)', // Frosted glass effect
    boxShadow: '0 8px 30px rgba(0, 0, 0, 0.2)',
    zIndex: 9999,
  },

  headerText: {
    fontSize: '2.5rem',
    fontWeight: '700',
    color: '#eee',
    letterSpacing: '1px',
    textTransform: 'capitalize',
  },

  gradientText: {
    background: 'linear-gradient(to right, #6a11cb, #2575fc)', // Gradient for the "Bakhshi"
    WebkitBackgroundClip: 'text',
    color: 'transparent',
  },

  logoContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '15px', // Even spacing between icons
  },

  logoLink: {
    display: 'inline-block',
    transition: 'transform 0.3s ease, opacity 0.3s ease',
  },

  logo: {
    width: '40px',
    height: '40px',
    borderRadius: '8px', // Subtle rounded corners
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.2)',
    transition: 'transform 0.3s ease, filter 0.3s ease',
    filter: 'grayscale(100%)',
    cursor: 'pointer',
  },
};

export default Header;
