import * as React from 'react';
import { Timeline, TimelineItem, TimelineSeparator, TimelineConnector, TimelineContent, TimelineOppositeContent } from '@mui/lab';
import Typography from '@mui/material/Typography';

import untitled from './image/Untitled.png';
import MosGames from "./image/Mo's Games logo.png";
import mohammedGithub from './image/Mohammed_github.png';
import theresidentsclub from './image/theresidentsclub.png';
import clearear from'./image/Clearear.jpg';
import Javascript from './image/javascript.png';
import react from './image/React.png';
import Python from './image/python.png';
import SQL from './image/sql-logo.png';
import kotlin from './image/kotlin.png';
import selenium from './image/Selenium.png';
import renuem from './image/Renuem.png'

const MainContent = () => {
  const [isMobileView, setIsMobileView] = React.useState(false);
  const [tooltipVisible, setTooltipVisible] = React.useState({
    javascript: false,
    react: false,
    python: false,
    sql: false,
    kotlin: false,
    selenium: false,
  });

  React.useEffect(() => {
    const checkIsMobile = () => {
      const isMobile = window.innerWidth <= 1050;
      setIsMobileView(isMobile);
    };

    checkIsMobile();

    window.addEventListener('resize', checkIsMobile);

    return () => {
      window.removeEventListener('resize', checkIsMobile);
    };
  }, []);

  const handleLogoHover = (language) => {
    setTooltipVisible({ ...tooltipVisible, [language]: true });
    document.getElementById(language).style.filter = 'grayscale(0%)';
  };

  const handleLogoLeave = (language) => {
    setTooltipVisible({ ...tooltipVisible, [language]: false });
    document.getElementById(language).style.filter = 'grayscale(100%)';
  };

  const mainContentStyle = {
    backgroundColor: '#171717',
    color: '#aaa',
    textAlign: 'center',
    padding: '2rem',
    overflowY: 'auto',
    height: '100vh',
    position: 'relative',
    scrollbarWidth: 'none',
    msOverflowStyle: 'none',
    '&::-webkit-scrollbar': {
      width: '0px',
    },
  };

  const linksContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginLeft:'-15px',
    marginBottom: '0rem',
    gap: isMobileView ? '50%' : '40%',
  };

  const linkStyle = {
    fontSize: isMobileView ? '80%' : '120%',
    marginRight: isMobileView ? '0' : '-2%',
    cursor: 'pointer',
    textDecoration: 'none',
    color: '#aaa',
    borderBottom: '2px solid #aaa',
    transition: 'color 0.3s, border-bottom-color 0.3s',
  };

  const hoverLinkStyle = {
    ...linkStyle,
    '&:hover': {
      color: '#C4DCFF',
      borderBottom: '2px solid #000',
      
      
    },
  };

  const imageContainerStyle = {
    marginTop: '4rem',
    display: 'flex',
    gap: '5%',
    marginLeft: '35%',
  };

  const imageStyle = {
    width: isMobileView ? '50%' : '15%',
    height: '15%',
    borderRadius: '5%',
    maxWidth: '100%',
  };

  const pcParagraphStyle = {
    textAlign: 'left',
    marginTop: '0rem',
    width: '50%',
    fontSize: '20px',
    display: isMobileView ? 'none' : 'block',
  };

  const logosContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '2rem',
    marginBottom: '2rem',
    gap: isMobileView ? '10%' : '10%',
    position:'relative',
  };

  const SmallLogoStyle = {
    height: isMobileView ? '25px' : '30px',
    margin: isMobileView ? '5px' : '5px',
    marginLeft: isMobileView ? '0' : '35px',
    borderRadius: '10px',
    transition: 'filter 0.3s',
    filter: 'grayscale(100%)',
  };

  const logoStyle = {
    width: isMobileView ? '30px' : '50px',
    height: 'auto',
    transition: 'filter 0.3s',
    filter: 'grayscale(100%)',
  };

  const tooltipTextStyle = {
    visibility: 'hidden',
    width: 'auto',
    background: '#555',
    color: '#fff',
    textAlign: 'center',
    borderRadius: '6px',
    padding: '5px 10px',
    position: 'absolute',
    zIndex: '1',
    bottom: '130%',
    right: '50%',
    transform: 'translateX(50%)',
    opacity: '0',
    transition: 'opacity .2s ease-in',
    fontSize: '14px',
    whiteSpace: 'nowrap',
  };

  const getTooltipStyle = (language) => ({
    ...tooltipTextStyle,
    visibility: tooltipVisible[language] ? 'visible' : 'hidden',
    opacity: tooltipVisible[language] ? '1' : '0',
  });

  return (
    <>
      <style>
        {`
          ::selection {
            color: #C4DCFF;
          }
          a:hover {
            color: #C4DCFF;
            border-bottom-color: #C4DCFF;
          }
        `}
      </style>
      <div style={{ position: 'relative' }}>
        <div style={mainContentStyle}>
          <div style={linksContainerStyle}>
          <a
              href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
              target="_blank"
              rel="noopener noreferrer"
              style={hoverLinkStyle}
              onMouseEnter={(e) => e.target.style.color = '#C4DCFF'}
              onMouseLeave={(e) => e.target.style.color = '#aaa'}
            >
              View CV
            </a>
            <a
              href="/"
              style={hoverLinkStyle}
              onMouseEnter={(e) => e.target.style.color = '#C4DCFF'}
              onMouseLeave={(e) => e.target.style.color = '#aaa'}
            >
              Home
            </a>
          
          </div>
          <div style={imageContainerStyle}>
            <img
              src={untitled}
              alt="Untitled"
              style={imageStyle}
            />
            <p style={pcParagraphStyle}>
              Hey, my name is  Mohammed. I've been working as a data analyst and web developer for over 5 years.
