import React from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between', // Space out elements: name left, logos right
    alignItems: 'center', // Vertically center both the name and logos
    position: 'sticky', // Make the header fixed at the top
    top: 0,
    width: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.0)', // Semi-transparent background for contrast
  },

  headerText: {
    fontSize: '2.5rem', // Large font size for your name
    fontWeight: 'bold', // Bold text
    color: '#fff', // White text color for contrast
  },

  logoContainer: {
    display: 'flex',
    justifyContent: 'space-evenly', // Space out the logos evenly
    alignItems: 'center', // Vertically center the logos
  },

  logo: {
    width: '40px', // Set size for the logos
    height: '40px',
    borderRadius: '50%', // Circular logos
    filter: 'grayscale(100%)', // Start with grayscale logos
    cursor: 'pointer',
    transition: 'filter 0.3s', // Smooth transition for the hover effect
    marginLeft: '15px', // Space out the logos
  },
};

const Header = () => {
  return (
    <div style={styles.header}>
      {/* Mohammed Bakhshi Name */}
      <div style={styles.headerText}>Mohammed Bakhshi</div>

      {/* Social Media Logos */}
      <div style={styles.logoContainer}>
        {[
          { href: 'https://www.linkedin.com/in/mohammed-bakhshi/', imgSrc: linkedin, alt: 'LinkedIn' },
          { href: 'https://github.com/Mohammed-Bakhshi', imgSrc: github, alt: 'GitHub' },
          { href: 'https://www.facebook.com/mohammed.bakhshi/', imgSrc: facebook, alt: 'Facebook' },
          { href: 'https://instagram.com/mohammedbakhshi/', imgSrc: instagram, alt: 'Instagram' },
          { href: 'mailto:mohammedbakhshi@hotmail.com', imgSrc: email, alt: 'Email' },
        ].map(({ href, imgSrc, alt }) => (
          <a key={alt} href={href} target="_blank" rel="noopener noreferrer">
            <img
              src={imgSrc}
              alt={alt}
              style={styles.logo}
              onMouseEnter={(e) => (e.target.style.filter = 'grayscale(0%)')}
              onMouseLeave={(e) => (e.target.style.filter = 'grayscale(100%)')}
            />
          </a>
        ))}
      </div>
    </div>
  );
};

export default Header;
