import React, { useState, useEffect, useRef } from "react";
import "./Project.css"; // Updated styles here
import {
  FaSolarPanel,
  FaClinicMedical,
  FaMusic,
  FaGamepad,
  FaFileSignature,
  FaUserCircle,
} from "react-icons/fa";

const Project = () => {
  const [visibleSection, setVisibleSection] = useState(false);
  const projectContainerRef = useRef(null);

  // Intersection Observer to trigger fade-up animation for the project container
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setVisibleSection(true);
        }
      });
    }, {
      threshold: 0.5
    });

    if (projectContainerRef.current) {
      observer.observe(projectContainerRef.current);
    }

    return () => {
      if (projectContainerRef.current) {
        observer.unobserve(projectContainerRef.current);
      }
    };
  }, []);

  const projects = [
    {
      id: 1,
      icon: <FaSolarPanel size={40} />,
      title: "Renuem",
      description: "Invest in renewable energy like wind farms and solar panels.",
      githubLink: "https://reneum.com/",
    },
    {
      id: 2,
      icon: <FaClinicMedical size={40} />,
      title: "Clear Ear",
      description: "Streamline appointment scheduling for hearing clinics.",
      githubLink: "https://github.com/your-repo/clear-ear",
    },
    {
      id: 3,
      icon: <FaMusic size={40} />,
      title: "Billboard Data Analysis",
      description: "Analyze decades of Billboard Hot 100 music trends.",
      githubLink: "https://github.com/Mohammed-Bakhshi/web-scraping-Billboard-hot-100",
    },
    {
      id: 4,
      icon: <FaGamepad size={40} />,
      title: "Mo’s Games",
      description: "Gaming website showcasing products and customer engagement.",
      githubLink: "https://www.facebook.com/mogamesdha",
    },
    {
      id: 5,
      icon: <FaFileSignature size={40} />,
      title: "Snap & Sign",
      description: "Real-time document signing and sharing platform.",
      githubLink: "https://github.com/your-repo/snap-sign",
    },
    {
      id: 6,
      icon: <FaUserCircle size={40} />,
      title: "Digital Portfolio",
      description: "Showcase of professional and personal projects.",
      githubLink: "https://github.com/Mohammed-Bakhshi/Digital-Portfolio",
    },
  ];

  return (
    <div className={`project-container ${visibleSection ? 'fade-up' : ''}`} ref={projectContainerRef}>
      <h1 className="projects-header">My Projects</h1>
      <div className="projects-grid">
        {projects.map((project) => (
          <div key={project.id} className="project-card">
            <div className="project-icon">{project.icon}</div>
            <div className="project-details">
              <h3 className="project-title">{project.title}</h3>
              <p className="project-description">{project.description}</p>
              <a
                href={project.githubLink}
                target="_blank"
                rel="noopener noreferrer"
                className="project-link"
              >
                View Details
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Project;
