import React, { useEffect, useState } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        if (entry.isIntersecting) {
          setIsVisible(true);
        } else {
          setIsVisible(false);
        }
      },
      { threshold: 0.1 }
    );
    observer.observe(document.querySelector('#header'));
    return () => observer.disconnect();
  }, []);

  return (
    <div
      id="header"
      style={{
        ...styles.header,
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(-20px)',
        transition: 'opacity 0.6s ease-out, transform 0.6s ease-out',
      }}
    >
      {/* Header text with sleek, modern typography */}
      <div style={styles.headerText}>Mohammed Bakhshi</div>

      {/* Social Media Icons with refined hover effects */}
      
      </div>
    
  );
};

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '25px 50px', // More balanced padding
    position: 'sticky',
    top: 0,
    width: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.1)', // Lighter background, still with transparency
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)', // Subtle shadow for depth
    zIndex: 9999,
    transition: 'background-color 0.3s ease', // Smooth transition for background color on scroll
  },

  headerText: {
    fontSize: '2.8rem',
    fontWeight: '700',
    color: '#fff',
    letterSpacing: '1px',
    textTransform: 'uppercase',
    transition: 'color 0.3s ease',
    paddingTop: '5px', // Slight top padding to center the text better vertically
  },

  logoContainer: {
    display: 'flex',
    justifyContent: 'space-evenly',
    alignItems: 'center',
  },

  logoLink: {
    display: 'inline-block',
    marginLeft: '20px', // More spaced out for modern aesthetics
    transition: 'all 0.3s ease', // Smooth transition for hover
  },

 
};

export default Header;
