import React, { useState, useEffect } from "react";
import "./mosgames.css";
import mosImage1 from "./image/Mo's Games logo.jpg";
import mosImage2 from "./image/MosGamesOpening.jpg";
import mosImage3 from "./image/MosGamesTournament.jpg";
import GaugeChart from "react-gauge-chart";

const MosGames = () => {
  const mosGamesDetails = [
    "Developed Mo's Games, a gaming lounge website offering console, arcade, and VR gaming experiences.",
    "Integrated features for gaming reservations, tracking game time, and providing a seamless user experience.",
    "Led the team in developing a user-friendly interface, enhancing the overall customer experience with dynamic features.",
  ];

  const images = [mosImage1, mosImage2, mosImage3];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      handleNext();
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  // Gauge state and animation
  const [htmlPercent, setHtmlPercent] = useState(0);
  const [cssPercent, setCssPercent] = useState(0);
  const [jsPercent, setJsPercent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setHtmlPercent((prev) => {
        if (prev < 0.8) return Math.min(prev + 0.01, 0.8);
        clearInterval(interval);
        return prev;
      });
    }, 50);

    const cssInterval = setInterval(() => {
      setCssPercent((prev) => {
        if (prev < 0.7) return Math.min(prev + 0.01, 0.7);
        clearInterval(cssInterval);
        return prev;
      });
    }, 50);

    const jsInterval = setInterval(() => {
      setJsPercent((prev) => {
        if (prev < 0.6) return Math.min(prev + 0.01, 0.6);
        clearInterval(jsInterval);
        return prev;
      });
    }, 50);
  }, []);

  return (
    <div className="mosgames-container">
      <div className="image-carousel">
        <button onClick={handlePrev} className="carousel-button prev-button">
          Prev
        </button>
        <img
          src={images[currentImageIndex]}
          alt="Mo's Games"
          className="carousel-image"
        />
        <button onClick={handleNext} className="carousel-button next-button">
          Next
        </button>
      </div>

      <div className="mosgames-details">
        <h2>Mo's Games Overview</h2>
        <ul>
          {mosGamesDetails.map((detail, index) => (
            <li key={index}>{detail}</li>
          ))}
        </ul>
      </div>

      <div className="gauge-chart-container">
        <div className="gauge-chart">
          <h3>HTML Skill Level</h3>
          <GaugeChart id="html-gauge" percent={htmlPercent} />
        </div>
        <div className="gauge-chart">
          <h3>CSS Skill Level</h3>
          <GaugeChart id="css-gauge" percent={cssPercent} />
        </div>
        <div className="gauge-chart">
          <h3>JavaScript Skill Level</h3>
          <GaugeChart id="js-gauge" percent={jsPercent} />
        </div>
      </div>
    </div>
  );
};

export default MosGames;
