import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import anime from 'animejs/lib/anime.es.js';
import Footer from './footer';

const HomePage = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const textWrapperRef = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };

    const animateText = () => {
      if (textWrapperRef.current) {
        textWrapperRef.current.innerHTML = textWrapperRef.current.textContent.replace(/\S/g, "<span class='letter'>$&</span>");
        anime.timeline({ loop: true })
          .add({
            targets: '.ml13 .letter',
            translateY: [100, 0],
            opacity: [0, 1],
            easing: 'easeOutExpo',
            duration: 1400,
            delay: (el, i) => 30 * i,
          })
          .add({
            targets: '.ml13 .letter',
            translateY: [0, -100],
            opacity: [1, 0],
            easing: 'easeInExpo',
            duration: 1200,
            delay: (el, i) => 30 * i,
          });
      }
    };

    handleResize();
    animateText();

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const commonStyle = {
    color: '#00FF00',  // Classic green text
    backgroundColor: '#1A1A1A', // Dark background
  };

  const pipBoyFrameStyle = {
    border: '5px solid #00FF00',
    padding: '200px',
    borderRadius: '10px',
    boxShadow: '0 0 10px 0 rgba(0, 255, 0, 0.5)',
    maxWidth: '80%',
    margin: '0 auto',
  };

  const sidebarStyle = {
    position: 'fixed',
    left: '10px',
    top: '20%',
    backgroundColor: '#2D2D2D',
    color: '#00FF00',
    padding: '20px',
    width: '200px',
    height: 'auto',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 255, 0, 0.5)',
  };

  const systemLogStyle = {
    position: 'absolute',
    bottom: '0',
    left: '0',
    width: '100%',
    color: '#00FF00',
    fontFamily: "'Courier New', Courier, monospace",
    padding: '10px',
    backgroundColor: '#171717',
  };

  return (
    <div style={commonStyle}>
      <div style={pipBoyFrameStyle}>
        <header style={{ textAlign: 'center', marginBottom: '30px' }}>
          <h1 style={{ fontSize: isMobileView ? '25px' : '40px', fontWeight: 'bold' }}>
            Hi, I'm Mohammed ðŸ‘‹
          </h1>
          <p style={{ fontSize: isMobileView ? '18px' : '24px' }}>
            I have been coding and analyzing data for 5+ years ðŸ¤–
          </p>
        </header>

        <section ref={textWrapperRef} className="ml13" style={{ margin: '2rem 0' }}>
          Available to work<br />
          <span style={{ fontSize: '16px', color: '#bbb' }}>Flexible salary</span>
        </section>

        <div style={{ margin: '2rem', display: 'flex', justifyContent: 'center', gap: '1rem' }}>
          <Link
            to="/about"
            style={{
              padding: '10px 20px',
              border: '2px solid #00FF00',
              color: '#00FF00',
              textDecoration: 'none',
              borderRadius: '5px',
              fontSize: '18px',
              transition: 'background-color 0.3s, color 0.3s',
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#003300';
              e.target.style.color = '#00FF00';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#00FF00';
            }}
          >
            About Me
          </Link>
          <a
            href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
            download="Mohammed_Bakhshi_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
            style={{
              padding: '10px 20px',
              border: '2px solid #00FF00',
              color: '#00FF00',
              textDecoration: 'none',
              borderRadius: '5px',
              fontSize: '18px',
              transition: 'background-color 0.3s, color 0.3s',
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#003300';
              e.target.style.color = '#00FF00';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#00FF00';
            }}
          >
            View CV
          </a>
        </div>
      </div>

      {/* Sidebar */}
      <div style={sidebarStyle}>
        <h2>Interests</h2>
        <ul>
          <li>Web Development</li>
          <li>Data Science</li>
          <li>Machine Learning</li>
          <li>Gaming</li>
          <li>Reading</li>
        </ul>
      </div>

      {/* System Log */}
      <div style={systemLogStyle}>
        <p>System Log: <span>Booting...</span></p>
        <p><span>Welcome back, User.</span></p>
        <p><span>All systems operational.</span></p>
      </div>

      <Footer />
    </div>
  );
};

export default HomePage;
