import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Footer from './footer';
import './HomePage.css'; // Separate CSS for keyframe animations

const HomePage = () => {
  const [isMobileView, setIsMobileView] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 1050);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const commonStyle = {
    backgroundColor: '#0D1117',
    color: '#C9D1D9',
    minHeight: '100vh',
    fontFamily: "'Roboto', sans-serif",
    padding: '20px',
    boxSizing: 'border-box',
    overflowX: 'hidden',
    position: 'relative',
  };

  const headerStyle = {
    position: 'absolute',
    top: '50%', // Center vertically
    left: '50%', // Center horizontally
    transform: 'translate(-50%, -50%)', // Offset to truly center
    textAlign: 'center',
    color: '#58A6FF',
  };

  const buttonContainerStyle = {
    display: 'flex',
    gap: '20px',
    marginTop: '10px',
  };

  const buttonStyle = {
    padding: '15px 30px',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    color: '#FFFFFF',
    background: 'linear-gradient(90deg, #1F6FEB, #58A6FF)',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'inline-block',
    transition: 'transform 0.3s',
  };

  const panelContainerStyle = {
    display: 'flex',
    justifyContent: 'space-around', // Spread panels horizontally
    alignItems: 'center',
    marginTop: '10%',
  };

  const sidePanelStyle = {
    backgroundColor: '#21262D',
    color: '#C9D1D9',
    padding: '20px',
    borderRadius: '12px',
    height: 'auto',
    width: '30%',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    textAlign: 'left',
  };

  return (
    <div style={commonStyle}>
      {/* Header and Buttons */}
      <div style={headerStyle}>
        <h1>Hi, I'm Mohammed ðŸ‘‹</h1>
        <div style={buttonContainerStyle}>
          <Link to="/about" style={buttonStyle}>
            About Me
          </Link>
          <a
            href="https://react--portfolio.s3.eu-west-2.amazonaws.com/Mohammed+Bakhshi+CV+.pdf"
            download="Mohammed_Bakhshi_CV.pdf"
            target="_blank"
            rel="noopener noreferrer"
            style={buttonStyle}
          >
            View CV
          </a>
        </div>
      </div>

      {/* Panels */}
      <div style={panelContainerStyle}>
        {/* Interests Panel */}
        <div style={sidePanelStyle}>
          <h3>Interests</h3>
          <ul>
            <li>Web Development</li>
            <li>Data Science</li>
            <li>Machine Learning</li>
            <li>Gaming</li>
            <li>Reading</li>
          </ul>
        </div>

        {/* Technical Skills Panel */}
        <div style={sidePanelStyle}>
          <h3>Technical Skills</h3>
          <ul>
            <li>React.js</li>
            <li>Node.js</li>
            <li>Python</li>
            <li>SQL</li>
            <li>RESTful APIs</li>
          </ul>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default HomePage;
