import React, { useEffect, useState } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        setIsVisible(entry.isIntersecting);
      },
      { threshold: 0.1 }
    );
    observer.observe(document.querySelector('#header'));
    return () => observer.disconnect();
  }, []);

  return (
    <div
      id="header"
      style={{
        ...styles.header,
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? 'translateY(0)' : 'translateY(-20px)',
        transition: 'opacity 0.6s ease-in-out, transform 0.6s ease-in-out',
      }}
    >
      <div style={styles.textContainer}>
        <h1 style={styles.title}>
          Mohammed <span style={styles.lastName}>Bakhshi</span>
        </h1>
        <p style={styles.tagline}>Full-Stack Developer | Crafting Scalable Solutions</p>
      </div>

      <div style={styles.socialIcons}>
        {[
          { href: 'https://www.linkedin.com/in/mohammed-bakhshi/', imgSrc: linkedin, alt: 'LinkedIn' },
          { href: 'https://github.com/Mohammed-Bakhshi', imgSrc: github, alt: 'GitHub' },
          { href: 'https://www.facebook.com/mohammed.bakhshi/', imgSrc: facebook, alt: 'Facebook' },
          { href: 'https://instagram.com/mohammedbakhshi/', imgSrc: instagram, alt: 'Instagram' },
          { href: 'mailto:mohammedbakhshi@hotmail.com', imgSrc: email, alt: 'Email' },
        ].map(({ href, imgSrc, alt }) => (
          <a key={alt} href={href} target="_blank" rel="noopener noreferrer" style={styles.iconLink}>
            <img
              src={imgSrc}
              alt={alt}
              style={styles.icon}
              onMouseEnter={(e) => (e.target.style.transform = 'scale(1.2)')}
              onMouseLeave={(e) => (e.target.style.transform = 'scale(1)')}
            />
          </a>
        ))}
      </div>
    </div>
  );
};

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px 50px',
    position: 'sticky',
    top: 0,
    width: '100%',
    backgroundColor: 'rgba(10, 10, 10, 0.85)', // Dark transparent background
    backdropFilter: 'blur(15px)', // Glassmorphism effect
    boxShadow: '0 8px 30px rgba(0, 0, 0, 0.2)',
    zIndex: 9999,
  },
  textContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '5px',
  },
  title: {
    fontSize: '3.5rem',
    fontWeight: 'bold',
    color: '#fff',
  },
  lastName: {
    background: 'linear-gradient(to right, #6a11cb, #2575fc)', // Gradient effect for last name
    WebkitBackgroundClip: 'text',
    color: 'transparent',
  },
  tagline: {
    fontSize: '1.2rem',
    color: '#ccc',
    fontStyle: 'italic',
  },
  socialIcons: {
    display: 'flex',
    gap: '15px',
  },
  iconLink: {
    transition: 'transform 0.3s ease, opacity 0.3s ease',
  },
  icon: {
    width: '40px',
    height: '40px',
    filter: 'grayscale(100%)',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'transform 0.3s ease, filter 0.3s ease',
  },
};

export default Header;
