import React, { useState, useEffect, useRef } from 'react';
import email from './image/email.png';
import linkedin from './image/likedin.png';
import github from './image/github.png';
import facebook from './image/Facebook.png';
import instagram from './image/instagram.png';

const mainContentStyle = {
  backgroundColor: '#171717',
  color: '#aaa',
  textAlign: 'center',
  padding: '2rem',
  minHeight: '100vh',
  overflowY: 'scroll',
  position: 'relative',
  scrollbarWidth: 'none',
  msOverflowStyle: 'none',
};

const Portfolio = () => {
  const [isMobileView, setIsMobileView] = useState(false);
  const [jokesOpened, setJokesOpened] = useState(Array(6).fill(false));
  const jokes = [
    "Why don't skeletons fight each other? ðŸ¦´ They don't have the guts!",
    "Why did the scarecrow win an award? ðŸ† He was outstanding in his field!",
    "Why can't your nose be 12 inches long? ðŸ‘ƒ Because then it would be a foot!",
    "How does a penguin build its house? ðŸ§ Igloos it together!",
    "Why did the bicycle fall over? ðŸš² Because it was two-tired!",
    "What do you call fake spaghetti? ðŸ An impasta!"
  ];

  useEffect(() => {
    const checkIsMobile = () => {
      const isMobile = window.innerWidth <= 1050;
      setIsMobileView(isMobile);
    };
    checkIsMobile();
    window.addEventListener('resize', checkIsMobile);
    return () => window.removeEventListener('resize', checkIsMobile);
  }, []);

  const handleEnvelopeClick = (index) => {
    const updatedJokesOpened = [...jokesOpened];
    updatedJokesOpened[index] = !updatedJokesOpened[index];
    setJokesOpened(updatedJokesOpened);
  };

  return (
    <div style={{ position: 'relative' }}>
      <div style={mainContentStyle}>
        <h1>Contact Me</h1>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap', marginTop: '20px' }}>
          {jokes.map((joke, index) => (
            <div
              key={index}
              onClick={() => handleEnvelopeClick(index)}
              style={{
                width: '100px',
                height: '80px',
                background: jokesOpened[index] ? '#f0e68c' : '#aaa',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                cursor: 'pointer',
                position: 'relative',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                transition: 'all 0.3s ease',
              }}
            >
              {jokesOpened[index] ? (
                <span style={{ fontSize: '12px', padding: '5px', color: '#333', textAlign: 'center' }}>{joke}</span>
              ) : (
                <span style={{ fontSize: '18px', color: '#fff' }}>ðŸ“©</span>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
